import { BrowserRouter as Router, Routes, Route, Navigate, useLocation } from 'react-router-dom';
import { useState, useEffect } from 'react';
import Header from './components/Header';
import IrisPredictForm from './components/IrisPredictForm';
import ResultPredictForm from './components/Cifar10PredictForm';
import Home from './components/Home';
import Register from './pages/Register';
import Login from './pages/Login';

const App = () => {
  const [isLoggedIn, setIsLoggedIn] = useState(() => {
    return localStorage.getItem('isLoggedIn') === 'true';
  });

  useEffect(() => {
    localStorage.setItem('isLoggedIn', isLoggedIn);
  }, [isLoggedIn]);

  const location = useLocation(); // Current route bilgisine ulaşmak için useLocation hook'unu kullanıyoruz.

  const shouldShowHeader = !['/login', '/register'].includes(location.pathname);

  return (
    <Router>
      {shouldShowHeader && <Header />} {/* Login ve Register sayfalarında Header'ı gizle */}
      <Routes>
        <Route path="/" element={isLoggedIn ? <Home /> : <Navigate to="/register" />} />
        <Route path="/iris-model" element={<IrisPredictForm modelType="iris_model" />} />
        <Route path="/result-model" element={<ResultPredictForm modelType="result_model" />} />
        <Route
          path="/register"
          element={isLoggedIn ? <Navigate to="/" /> : <Register setIsLoggedIn={setIsLoggedIn} />}
        />
        <Route
          path="/login"
          element={isLoggedIn ? <Navigate to="/" /> : <Login setIsLoggedIn={setIsLoggedIn} />}
        />
      </Routes>
    </Router>
  );
};

export default App;
