import React, { useState } from 'react';
import axios from 'axios';

// Utility function to convert image to a 32x32x3 matrix
const processImage = async (file) => {
  const img = new Image();
  const canvas = document.createElement('canvas');
  const ctx = canvas.getContext('2d');
  const reader = new FileReader();

  return new Promise((resolve, reject) => {
    reader.onload = () => {
      img.src = reader.result;
    };

    img.onload = () => {
      canvas.width = 32;
      canvas.height = 32;
      ctx.drawImage(img, 0, 0, 32, 32);

      const imageData = ctx.getImageData(0, 0, 32, 32);
      const data = imageData.data;
      const matrix = [];

      for (let i = 0; i < 32; i++) {
        const row = [];
        for (let j = 0; j < 32; j++) {
          const index = (i * 32 + j) * 4;
          const r = data[index] / 255;
          const g = data[index + 1] / 255;
          const b = data[index + 2] / 255;
          row.push([r, g, b]); // RGB values
        }
        matrix.push(row);
      }

      resolve(matrix);
    };

    img.onerror = (err) => reject(err);
    reader.readAsDataURL(file);
  });
};

const ResultPredictForm = () => {
  const [imageData, setImageData] = useState(null);
  const [imagePreview, setImagePreview] = useState(null); // New state for image preview
  const [response, setResponse] = useState(null);
  const [error, setError] = useState(null);

  const handleImageChange = async (e) => {
    const file = e.target.files[0];
    if (file) {
      try {
        const imgData = await processImage(file);
        console.log("Image data processed:", imgData); // Debugging
        setImageData(imgData);

        // Create a preview URL and set it to state
        const previewURL = URL.createObjectURL(file);
        setImagePreview(previewURL);
      } catch (err) {
        console.error("Error processing image", err);
        setError("Failed to process image");
      }
    }
  };

  const handleSubmit = async (event) => {
    event.preventDefault();
    if (!imageData) {
      setError("No image data available");
      return;
    }

    try {
      const result = await axios.post('http://localhost:8000/predict/cifar10_model', {
        conv2d_input: imageData
      }, {
        headers: {
          'Content-Type': 'application/json',
        },
        timeout: 5000
      });

      console.log("Response data:", result.data);

      if (result.data && result.data.predicted_class) {
        const predictedClass = result.data.predicted_class;
        setResponse({ predictedClass: predictedClass });
      } else {
        console.log("Predictions data is not available.");
        setError("Prediction data is not available.");
      }
    } catch (error) {
      console.log("Error details:", error);
      setError(error.response ? error.response.data : 'An error occurred');
    }
  };

  return (
    <div className='flex justify-center '>
      <form onSubmit={handleSubmit}>
        <label class="block mb-2 text-l font-medium text-gray-900" for="file_input">Upload Image for Prediction</label>
        <input type="file" accept="image/*" class=" text-gray-400 font-semibold text-sm bg-white border file:cursor-pointer cursor-pointer file:border-0 file:py-3 file:px-4 file:mr-4 file:bg-gray-100 file:hover:bg-gray-200 file:text-gray-500 rounded" onChange={handleImageChange} />   
        <div className='flex justify-center my-4'>
          <button type="submit" class="text-white bg-green-700 hover:bg-green-800 focus:outline-none focus:ring-4 focus:ring-green-300 font-medium rounded-full text-sm px-5 py-2.5 text-center me-2 mb-2 dark:bg-green-600 dark:hover:bg-green-700 dark:focus:ring-green-800">Submit</button>
        </div>
      </form>
      {imagePreview && (
        <div>
          <h2>Image Preview:</h2>
          <img src={imagePreview} alt="Preview" style={{ width: '300px', height: '300px' }} />
        </div>
      )}
      {response && (
        <div>
          <p>Predicted Class: {response.predictedClass}</p>
        </div>
      )}
      {error && <div>Error: {JSON.stringify(error)}</div>}
    </div>
  );
};

export default ResultPredictForm;
