import React, { useState } from 'react';
import axios from 'axios';

// Utility function to convert image to a 32x32 matrix
const processImage = async (file) => {
  const img = new Image();
  const canvas = document.createElement('canvas');
  const ctx = canvas.getContext('2d');
  const reader = new FileReader();

  return new Promise((resolve, reject) => {
    reader.onload = () => {
      img.src = reader.result;
    };

    img.onload = () => {
      canvas.width = 32;
      canvas.height = 32;
      ctx.drawImage(img, 0, 0, 32, 32);

      const imageData = ctx.getImageData(0, 0, 32, 32);
      const data = imageData.data;
      const matrix = [];

      for (let i = 0; i < 32; i++) {
        const row = [];
        for (let j = 0; j < 32; j++) {
          const index = (i * 32 + j) * 4;
          const r = data[index] / 255;
          const g = data[index + 1] / 255;
          const b = data[index + 2] / 255;
          const avg = (r + g + b) / 3;
          row.push(avg);
        }
        matrix.push(row);
      }

      resolve(matrix);
    };

    img.onerror = (err) => reject(err);
    reader.readAsDataURL(file);
  });
};

const ResultPredictForm = () => {
  const [imageData, setImageData] = useState(null);
  const [response, setResponse] = useState(null);
  const [error, setError] = useState(null);

  const handleImageChange = async (e) => {
    const file = e.target.files[0];
    if (file) {
      try {
        const imgData = await processImage(file);
        console.log("Image data processed:", imgData); // Debugging
        setImageData(imgData);
      } catch (err) {
        console.error("Error processing image", err);
        setError("Failed to process image");
      }
    }
  };

  const handleSubmit = async (event) => {
    event.preventDefault();
    if (!imageData) {
      setError("No image data available");
      return;
    }
    try {
      const result = await axios.post('http://localhost:8000/predict/result_model', {
        conv2d_input: imageData
      }, {
        headers: {
          'Content-Type': 'application/json',
        },
      });
      console.log("Response data:", result.data);

      if (result.data && result.data.predicted_class && result.data.probabilities) {
        const predictions = result.data.probabilities;
        const predictedClass = result.data.predicted_class;
        setResponse({ predictedClass, probabilities: predictions });
      } else {
        console.log("Predictions data is not available.");
        setError("Prediction data is not available.");
      }
    } catch (error) {
      console.log("Error details:", error);
      setError(error.response ? error.response.data : 'An error occurred');
    }
  };

  return (
    <div>
      <form onSubmit={handleSubmit}>
        <h1>Upload Image for Prediction</h1>
        <input type="file" accept="image/*" onChange={handleImageChange} />
        <button type="submit">Submit</button>
      </form>
      {response && (
        <div>
          <p>Predicted Class: {response.predictedClass}</p>
          <p>Probabilities: {JSON.stringify(response.probabilities)}</p>
        </div>
      )}
      {error && <div>Error: {JSON.stringify(error)}</div>}
    </div>
  );
};

export default ResultPredictForm;
